import React, { useState, useEffect } from "react";
import {
  Box,
  Heading,
  Skeleton,
  UnorderedList,
  ListItem,
  Spinner,
  useToast,
} from "@chakra-ui/react";
const Home = () => {
  const [contacts, setContacts] = useState([]);
  
  const [loading, setLoading] = useState(false);
  const toast = useToast();

  useEffect(() => {
    loadContacts();
  }, []);

  const loadContacts = async () => {
    try {
      setLoading(true);
      const response = await fetch(`https://randomuser.me/api/?results=15`);
      const data = await response.json();
      setContacts((prevContacts) => [...prevContacts, ...data.results]);
      console.log(data.results);
    } catch (error) {
      console.error("Error loading contacts:", error);
      toast({
        title: "Error",
        description: "Failed to load contacts.",
        status: "error",
        duration: 3000,
        isClosable: true,
      });
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    const handleScroll = () => {
      if (
        window.innerHeight + window.pageYOffset >=
        document.documentElement.offsetHeight
      ) {
        // User has scrolled to the end of the page
        if (!loading) {
          setLoading(true);
          setTimeout(() => {
            loadContacts();
          }, 1000);
        }
      }
    };

    window.addEventListener("scroll", handleScroll);
    return () => {
      window.removeEventListener("scroll", handleScroll);
    };
  }, []);

  return (
    <Box pt={"4rem"} maxW="320px" mx="auto">
      <Heading textAlign="center" mb={4}>
        Infinite Scroll Example
      </Heading>
      <UnorderedList listStyleType="none" p={0}>
        {contacts.map((contact, index) => (
          <ListItem key={index} py={2} borderBottom="1px solid #ccc">
            {contact.name.first} {contact.name.last}
          </ListItem>
        ))}
      </UnorderedList>
      {loading && (
        <Box textAlign="center" mt={4}>
          {(() => {
            const skeletonElements = [];
            for (let i = 0; i < 10; i++) {
              skeletonElements.push(
                <React.Fragment key={i}>
                  <Skeleton mb={"5px"}>
                    <div>contents wrapped</div>
                    <div>won't be visible</div>
                  </Skeleton>
                </React.Fragment>
              );
            }
            return skeletonElements;
          })()}
        </Box>
      )}
    </Box>
  );
};

export default Home;
