import React, { useState, useEffect } from "react";
import {
  Box,
  Heading,
  UnorderedList,
  ListItem,
  Spinner,
  useToast,
} from "@chakra-ui/react";
const Home = () => {
  const [contacts, setContacts] = useState([]);
  const [loading, setLoading] = useState(false);
  const [page, setPage] = useState(1);
  const toast = useToast();

  useEffect(() => {
    loadContacts();
  }, []);

  const loadContacts = async () => {
    try {
      setLoading(true);
      const response = await fetch(
        `https://randomuser.me/api/?results=10&page=${page}`
      );
      const data = await response.json();
      setContacts((prevContacts) => [...prevContacts, ...data.results]);
      setPage((prevPage) => prevPage + 1);
    } catch (error) {
      console.error("Error loading contacts:", error);
      toast({
        title: "Error",
        description: "Failed to load contacts.",
        status: "error",
        duration: 3000,
        isClosable: true,
      });
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    const handleScroll = () => {
      if (
        window.innerHeight + window.pageYOffset >=
        document.documentElement.offsetHeight
      ) {
        // User has scrolled to the end of the page
        if (!loading) {
          setLoading(true);
          setTimeout(() => {
            loadContacts();
          }, 1000);
        }
      }
    };

    window.addEventListener("scroll", handleScroll);
    return () => {
      window.removeEventListener("scroll", handleScroll);
    };
  }, []);

  return (
    <Box p={4} maxW="320px" mx="auto">
      <Heading textAlign="center" mb={4}>
        Infinite Scroll Example
      </Heading>
      <UnorderedList listStyleType="none" p={0}>
        {contacts.map((contact, index) => (
          <ListItem key={index} py={2} borderBottom="1px solid #ccc">
            {contact.name.first} {contact.name.last}
          </ListItem>
        ))}
      </UnorderedList>
      {loading && (
        <Box textAlign="center" mt={4}>
          <Spinner size="md" />
        </Box>
      )}
    </Box>
  );
};

export default Home;
