import React, { useState, useEffect } from "react";
import {
  Box,
  Heading,
  Skeleton,
  UnorderedList,
  ListItem,
  Spinner,
  useToast,
} from "@chakra-ui/react";
const Home = () => {
  const [contacts, setContacts] = useState([]);
  const [pictures, setPictures] = useState([]);
  const [loading, setLoading] = useState(false);
  const toast = useToast();

  useEffect(() => {
    loadContacts();
  }, []);

  const loadContacts = async () => {
    try {
      setLoading(true);
      const response = await fetch(`https://randomuser.me/api/?results=15`);
      const data = await response.json();
      /* we are doing that way because we want every time when user fetch new data
        so that data should be come into new array and also we want previous data as well...
      */
      setContacts((prevContacts) => [...prevContacts, ...data.results]);
    } catch (error) {
      toast({
        title: "Error",
        description: "Failed to load contacts.",
        status: "error",
        duration: 3000,
        isClosable: true,
      });
    } finally {
      setLoading(false);
    }
  };

  console.log(contacts);
  useEffect(() => {
    const handleScroll = () => {
      if (
        window.innerHeight + window.pageYOffset >=
        document.documentElement.offsetHeight
      ) {
        if (!loading) {
          setLoading(true);
          setTimeout(() => {
            loadContacts();
          }, 1000);
        }
      }
    };

    window.addEventListener("scroll", handleScroll);
    return () => {
      window.removeEventListener("scroll", handleScroll);
    };
  }, []);

  return (
    <Box pt={"4rem"} maxW="320px" mx="auto">
      <Heading textAlign="center" mb={4}>
        Infinite Scroll Example
      </Heading>
      <UnorderedList listStyleType="none" p={0}>
        {contacts.map((contact, index) => (
          <ListItem key={index} py={2} borderBottom="1px solid #ccc">
            {contact.name.first} {contact.name.last}
          </ListItem>
        ))}
      </UnorderedList>
      {loading && (
        <Box textAlign="center" mt={4}>
          {(() => {
            const skeletonElements = [];
            for (let i = 0; i < 10; i++) {
              skeletonElements.push(
                <React.Fragment key={i}>
                  <Skeleton mb={"5px"}>
                    <div>contents wrapped</div>
                    <div>won't be visible</div>
                  </Skeleton>
                </React.Fragment>
              );
            }
            return skeletonElements;
          })()}
        </Box>
      )}
    </Box>
  );
};

export default Home;
