import React, { useState, useEffect } from "react";
import {
  Box,
  Heading,
  Skeleton,
  UnorderedList,
  ListItem,
  Spinner,
  useToast,
} from "@chakra-ui/react";
const Home = () => {
  const [users, setUsers] = useState([]);
  const [loading, setLoading] = useState(false);
  const toast = useToast();

  useEffect(() => {
    FetchAllUsers();
  }, []);

  const FetchAllUsers = () => {
    try {
      setLoading(true);
      fetch(`https://randomuser.me/api/?results=15`)
        .then((data) => data.json())
        .then((data) => {
          /* we are doing that way because we want every time when user fetch new data
          so that data should be come into new array and also we want previous data as well...
          */
          setUsers((prevusers) => [...prevusers, ...data.results]);
          setLoading(false);
        })
        .catch((error) => console.log(error));
    } catch (error) {
      toast({
        title: "Error",
        description: "Failed to load users.",
        status: "error",
        duration: 3000,
        isClosable: true,
      });
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    const handleScroll = () => {
      if (
        window.innerHeight + window.pageYOffset >=
        document.documentElement.offsetHeight
      ) {
        // User has scrolled to the end of the page
        if (!loading) {
          setLoading(true);
          setTimeout(() => {
            FetchAllUsers();
          }, 1000);
        }
      }
    };

    window.addEventListener("scroll", handleScroll);
    return () => {
      window.removeEventListener("scroll", handleScroll);
    };
  }, []);

  return (
    <Box pt={"4rem"} maxW="320px" mx="auto">
      <Heading textAlign="center" mb={4}>
        Infinite Scroll Example
      </Heading>
      <Box p={0}>
        {users.map((contact, index) => (
          <Box key={index} py={2} borderBottom="1px solid #ccc">
            <Flex>
              <Text>
                {contact.name.first} {contact.name.last}
              </Text>
              <Image
                borderRadius="full"
                boxSize="50px"
                // src={user.picturePath}
                alt="Dan Abramov"
                objectFit={"cover"}
              />
            </Flex>
          </Box>
        ))}
      </Box>
      {loading && (
        <Box textAlign="center" mt={4}>
          {(() => {
            const skeletonElements = [];
            for (let i = 0; i < 10; i++) {
              skeletonElements.push(
                <React.Fragment key={i}>
                  <Skeleton mb={"5px"}>
                    <div>contents wrapped</div>
                    <div>won't be visible</div>
                  </Skeleton>
                </React.Fragment>
              );
            }
            return skeletonElements;
          })()}
        </Box>
      )}
    </Box>
  );
};

export default Home;
